let arr = str.split('');


// Question #1
let west = 0;
let east = 0;

for(i = 0; i < arr.length; i++) {
    if(arr[i] == '(') {
        west++;
    } else {
        east++;
    }
}

let displace;
if(west > east) {
    let val = west - east;
    displace = `West: ${val}`;
} else if(east > west) {
    let val = east - west;
    displace = `East: ${val}`;
} else {
    displace = 'Equal';
}
console.log(displace);

west = 0;
east = 0;
displace = '';


// Question #2
let direct;

for(i of arr) {
    if(i == '(' && east > west) {
        west += 1;
    } else if(i == ')') {
        east +=1;
    }
}

if(west > east) {
    direct = 'West: ';
} else if(east > west) {
    direct = 'East: ';
} else {
    direct = 'Equal';
}

displace = Math.abs(west - east);

if(direct != 'Equal') {
    console.log(`${direct}${displace}`);
} else {
    console.log('Equal');
}

west = 0;
east = 0;
displace = '';


// Question #3

for (let i = 0; i < arr.length; i++) {
    if(arr[i] == '(' && arr[i + 1] == '(') {
        west++;
        i++;
    } else if(arr[i] == '(') {
        west++;
    }

    if(arr[i] == ')' && arr[i + 1] == ')') {
        east++;
        i++;
    } else if(arr[i] == ')') {
        east++;
    }
}

if (west > east) {
    let val = west - east;
    displace = `West: ${val}`;
} else if (east > west) {
    let val = east - west;
    displace = `East: ${val}`;
} else {
    displace = 'Equal';
}
console.log(displace);